INSERT INTO board (board_no, mno, community_no, title, contents, reg_date, update_date, view_count) VALUES(48, 57, 1, '수원역 대포집 대박이에요!!', '<p>어제 처음갔는데 기본안주가 무려 라면이고</p><p>안주도 너무 맛있어서 술이 쭉쭉 들어갔어요</p><p>너무 잘놀았고 야술먹자 유저분들에게도 적극 추천합니다!!</p>', '2022-04-28 11:15:38.000', '2022-04-28 11:38:46.000', 4);
INSERT INTO board (board_no, mno, community_no, title, contents, reg_date, update_date, view_count) VALUES(51, 58, 2, '친구좀 찾아주세요!!', '<p>20분 전까지 같이 술마시고 있었는데 화장실갔다온다고 하더니 사라졌어요</p><p>술 진탕 취헤서 제 몸도 못가눌텐데</p><p>사고치기 전에 꼭 찾아주세요ㅠㅠ</p>', '2022-04-28 11:19:58.000', '2022-04-28 11:30:13.000', 2);
INSERT INTO board (board_no, mno, community_no, title, contents, reg_date, update_date, view_count) VALUES(52, 100, 3, '안녕하세요 반갑습니다~', '처음 옵니다~ 잘부탁드려요 맛있는 술 많이 먹어요~', '2022-04-28 11:22:31.000', '2022-04-28 11:22:31.000', 0);
INSERT INTO board (board_no, mno, community_no, title, contents, reg_date, update_date, view_count) VALUES(53, 58, 3, '오늘 롤만 해서 우울하다....', '<p>하루종일 게임만 하니까 우울하네...</p><p>심지어 탑킨드로 탑스카너한테 0/7/1 되니까 너무 슬프다ㅠㅠ</p>', '2022-04-28 11:22:40.000', '2022-04-28 11:36:34.000', 6);
INSERT INTO board (board_no, mno, community_no, title, contents, reg_date, update_date, view_count) VALUES(54, 100, 1, '망원동 술집 추천합니다', '<p>망원 한강 지구 입구 앞에 부산본가라는 술집이 있는데 사장님도 푸근하시고 분위기도 좋고 안주도 정말 맛있습니다 나중에 한번 가보세요~</p>', '2022-04-28 11:25:54.000', '2022-04-28 11:29:34.000', 3);
